            const express = require("express");

            const app = express();
            app.get("/", function(request, response){    
                response.send("\
Главная страница\");
            });
            app.get("/about", function(request, response){                
                response.send("\
О сайте\");
            });
            app.get("/contact", function(request, response){               
                response.send("\
Контакты\");
            });
            app.get("/main", function(request, response){               
                response.send("\
Главная страница\");
            });

            app.listen(3000);
